
public class LearningOperators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num1 = 12;
		int num2 = 4;

		// Additional operator
		int num3 = num1 + num2; //12+4=16
		System.out.println("The sum of num1 and num2 :" + num3);

		// Subtraction
		num3 = num1 - num2; //12-4=8
		System.out.println("The difference of num1 and num2:" + num3);

		// Multiplication
		num3 = num1 * num2; //12*4=48
		System.out.println("The product of num1 and num2: " + num3);

		// Division
		num3 = num3 / num2;  //48/4=12
		System.out.println("The quotient of num1 and num2: " + num3);

		// Modulus
		num3 = num1 % num2;  //12%4=0
		System.out.println("The remainder of num1and num2:" + num3);

	}

}
