
public class LearningWhileLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		// pick 5 th student from the range 1 to 20

		int rollNumberOfStudent = 1;
		while (rollNumberOfStudent <= 30) {
			System.out.println("Roll numbers that is picked:" + rollNumberOfStudent);
			rollNumberOfStudent += 5;
		}

	}

}
